hooks.hook:
if (file_exists($ext_info['path'].'/lang/'.S2_LANGUAGE.'.php'))
	require $ext_info['path'].'/lang/'.S2_LANGUAGE.'.php';
else
	require $ext_info['path'].'/lang/English.php';
$buttons = array_merge(array('refresh_hooks' => '<button class="bitbtn" style="background-image: url('.$ext_info['url'].'/r.png);" onclick="GETAsyncRequest(sUrl + \'action=s2_manage_extensions_refresh_hooks&id='.s2_htmlencode(addslashes($id)).'\'); return false;">'.$lang_s2_manage_extensions['Refresh hooks'].'</button>'), $buttons);

if ($action == 's2_manage_extensions_refresh_hooks')
{
	$is_permission = $s2_user['edit_users'];
	($hook = s2_hook('rq_action_load_extensions_start')) ? eval($hook) : null;
	s2_test_user_rights($is_permission);

	if (!isset($_GET['id']))
		die('Error in GET parameters.');
	$id = $_GET['id'];

	require 'extensions.php';
	require $ext_info['path'].'/functions.php';

	$messages = s2_manage_extensions_refresh_hooks($id);
	if (!empty($messages))
	{
		header('X-S2-Status: Error');
		echo implode("\n", $messages);
	}
}
