extension.install:
$s2_wysiwys_config = array(
	'S2_WYSIWYG_TYPE'		=> '0',
);

foreach ($s2_wysiwys_config as $conf_name => $conf_value)
{
	if (defined($conf_name))
		continue;

	$query = array(
		'INSERT'	=> 'name, value',
		'INTO'		=> 'config',
		'VALUES'	=> '\''.$conf_name.'\', \''.$conf_value.'\''
	);

	$s2_db->buildAndQuery($query);
}
